package jt1078

import (
	"bytes"
	"encoding/hex"
	"errors"
	"testing"
)

func TestDecode(t *testing.T) {
	type want struct {
		msg string
		err error
	}
	tests := []struct {
		name string
		args string
		want want
	}{
		{
			name: "原子包-视频I祯",
			args: "3031636481060000295696659617010000000000000000000000000000020000",
			want: want{
				msg: "",
				err: nil,
			},
		},
		{
			name: "错误数据",
			args: "2031636481e20000295696659617010000000000000000000000000000020000",
			want: want{
				msg: "2031636481e20000295696659617010000000000000000000000000000020000",
				err: ErrUnqualifiedData,
			},
		},
		{
			name: "head不足 最低标准长度16",
			args: "3031636481e200",
			want: want{
				msg: "3031636481e200",
				err: ErrHeaderLength2Short,
			},
		},
		{
			name: "head不足 不确定的数据",
			args: "3031636481e2000029569665961701000000000000000000000000000",
			want: want{
				msg: "3031636481e2000029569665961701000000000000000000000000000",
				err: ErrHeaderLength2Short,
			},
		},
		{
			name: "body不足",
			args: "3031636481e200002956966596170100000000000000000000000000000200",
			want: want{
				msg: "00",
				err: ErrBodyLength2Short,
			},
		},
		{
			name: "body盈余",
			args: "3031636481e200002956966596170100000000000000000000000000000200003031636481e20000295696659617010000000000000000000000000000020000",
			want: want{
				msg: "3031636481e20000295696659617010000000000000000000000000000020000",
				err: nil,
			},
		},
		{
			name: "视频I帧-分包处理时的第一个包",
			args: "3031636481e20000295696659617010100000000000000000000000000020000",
			want: want{
				msg: "",
				err: nil,
			},
		},
		{
			name: "视频I帧-分包处理时的中间包",
			args: "3031636481e20000295696659617010300000000000000000000000000020000",
			want: want{
				msg: "",
				err: nil,
			},
		},
		{
			name: "视频I帧-分包处理时的最后一个包",
			args: "3031636481e20000295696659617010200000000000000000000000000020000",
			want: want{
				msg: "",
				err: nil,
			},
		},
		{
			name: "透传数据",
			args: "3031636481e200002956966596170240000101",
			want: want{
				msg: "",
				err: nil,
			},
		},
		{
			name: "G711A",
			args: "",
			want: want{
				msg: "",
				err: nil,
			},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			data, _ := hex.DecodeString(tt.args)
			p := NewPacket()
			remainData, err := p.Decode(data)
			if !errors.Is(err, tt.want.err) {
				t.Errorf("Decode() = %s\n want %s", err, tt.want.err)
				return
			}
			wantData, _ := hex.DecodeString(tt.want.msg)
			if bytes.Compare(remainData, wantData) != 0 {
				t.Errorf("Decode() = %x\n want %x", remainData, wantData)
				return
			}
			_ = p.String()
		})
	}

}

func TestEncode(t *testing.T) {
	type args struct {
		sim     string
		channel uint8
		opt     func(p *Packet)
	}
	type want struct {
		result string
		err    error
	}
	tests := []struct {
		name string
		args args
		want want
	}{
		{
			name: "原子包-视频I祯",
			args: args{
				sim:     "295696659617",
				channel: 1,
				opt: func(p *Packet) {
					p.Flag.M = 1
					p.Flag.PT = PTH264
					p.DataType = DataTypeI
					data, _ := hex.DecodeString("0000")
					p.Body = data
				},
			},
			want: want{
				result: "3031636481e20000295696659617010000000000000000000000000000020000",
				err:    nil,
			},
		},
		{
			name: "透传数据",
			args: args{
				sim:     "295696659617",
				channel: 2,
				opt: func(p *Packet) {
					p.Flag.M = 1
					p.Flag.PT = PTH264
					p.DataType = DataTypePenetrate
					data, _ := hex.DecodeString("01")
					p.Body = data
				},
			},
			want: want{
				result: "3031636481e200002956966596170240000101",
				err:    nil,
			},
		},
		{
			name: "G711A",
			args: args{
				sim:     "1003",
				channel: 1,
				opt: func(p *Packet) {
					p.Seq = 300
					p.Timestamp = 1746418625361
					msg := ""
					data, _ := hex.DecodeString(msg)
					p.Body = data
				},
			},
			want: want{
				result: "",
				err:    nil,
			},
		},
		{
			name: "错误数据",
			args: args{
				sim:     "1003",
				channel: 1,
				opt: func(p *Packet) {
					p.ID = ""
				},
			},
			want: want{
				err: ErrUnqualifiedData,
			},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			p := NewCustomPacket(tt.args.sim, tt.args.channel, tt.args.opt)
			jt1078Data, err := p.Encode()
			if !errors.Is(err, tt.want.err) {
				t.Errorf("Encode() = %s\n want %s", err, tt.want.err)
				return
			}
			wantData, _ := hex.DecodeString(tt.want.result)
			if bytes.Compare(jt1078Data, wantData) != 0 {
				t.Errorf("Encode()\n got= %x\n want %x", jt1078Data, wantData)
				return
			}
		})
	}
}
